import { Component, OnInit } from '@angular/core';
import { notification } from '../../Models/notification';
import { LoginService } from '../../services/login.service';
import { Router } from '@angular/router';
import { NgForm } from '@angular/forms';
import { ToastController , LoadingController } from '@ionic/angular';
import { Storage } from '@ionic/storage';
import { Camera, CameraOptions } from '@ionic-native/camera/ngx';
declare var window;

@Component({
  selector: 'app-notification-add',
  templateUrl: './notification-add.page.html',
  styleUrls: ['./notification-add.page.scss'],
})

export class NotificationAddPage implements OnInit {
  userlist:any;
  public curruserid:string;
    //image to be displayed in template
  imageData;
  base64Image : string="../../assets/upload.png";

  notification : notification = {Title:'',Description:'',Type:'',User_id:'',Creation_date:new Date().toString(),Thumbnail:'',Url_Link:'',currentuser:''};
  constructor(private router:Router,private loginservice:LoginService,private toastcontroller:ToastController,public loadingController:LoadingController
  ,private storage: Storage,private camera: Camera) { 
  }

  ngOnInit() {
    this.getUserList();
  }

getUserList()
{
  this.storage.get('customer').then((val) => {
      if(val)
      {
        this.curruserid = val[0].id;
      }
    });

   this.loginservice.GetUsersList().subscribe(
            data => {
              console.log(data);
              this.userlist = data;
            });
}


openCamera(){
    const options: CameraOptions = {
    quality: 50,
    destinationType: this.camera.DestinationType.DATA_URL,
    encodingType: this.camera.EncodingType.JPEG,
    mediaType: this.camera.MediaType.PICTURE,
   }

    this.camera.getPicture(options).then((imageData) => {
    this.imageData = imageData;
    this.base64Image = "data:image/jpeg;base64,"+ imageData;
    
    }, (err) => {
       // Handle error
       alert("error "+JSON.stringify(err))
  });
  }

  openGallery()
  {
    const options: CameraOptions = {
      quality: 50,
      destinationType: this.camera.DestinationType.DATA_URL,
      encodingType: this.camera.EncodingType.JPEG,
      mediaType: this.camera.MediaType.PICTURE,
      sourceType:this.camera.PictureSourceType.PHOTOLIBRARY
     }
  
      this.camera.getPicture(options).then((imageData) => {
        this.imageData = imageData;
        this.base64Image = "data:image/jpeg;base64,"+ imageData;
        
      }, (err) => {
         // Handle error
         alert("error "+JSON.stringify(err))
    });
  }

dataURItoBlob(dataURI) {
    const byteString = window.atob(dataURI);
    const arrayBuffer = new ArrayBuffer(byteString.length);
    const int8Array = new Uint8Array(arrayBuffer);
    for (let i = 0; i < byteString.length; i++) {
      int8Array[i] = byteString.charCodeAt(i);
     }
    const blob = new Blob([int8Array], { type: 'image/jpeg' });    
   return blob;
  }

async onSubmit(notificationForm : NgForm)
{
    const loading = await this.loadingController.create({
      message: 'Please wait...'
    });

    if(this.base64Image != "")
    {    

    loading.present();
    
    const date = new Date().valueOf();
    const imageName = date+ '.jpeg';

    console.log(this.imageData);
    
    // call method that creates a blob from dataUri    
    //const imageBlob = this.dataURItoBlob(this.imageData);

    const imageBlob = ""
    
    console.log(imageBlob);
    
    const imageFile = new File([imageBlob], imageName, { type: 'image/jpeg' })
    let postData = new FormData();
    postData.append('file',imageFile);
    
    console.log(imageFile);

    this.notification.currentuser = this.curruserid;
    this.notification.Thumbnail = postData;

    console.log(this.notification);

    this.loginservice.AddNotifiction(this.notification).subscribe(
            data => {

              console.log(data);
              loading.dismiss();
              window.getnotifications.ngOnInit();
              this.router.navigate(['/notifications']);
            },
            err => { 
              if(err.error.text == "New record created successfully")
              {
                loading.dismiss();
                this.router.navigate(['/notifications']);
              }
            }
    );
    }else
    {
      this.toastcontroller.create({
        message: 'Please select image to upload',
        duration: 3000,
        position: 'bottom'
      }).then(toast=>toast.present());
    }
  }

}
